target.adhoc-task:
if (!class_exists('BoolPropertyDemo', false)) {
    class BoolPropertyDemo extends Task
    {
        protected $param;

        function setParam($value)
        {
            $this->param = $value;
        }

        function main()
        {
            $logMessage = 'Param type is ' . gettype($this->param);
            $this->log($logMessage);
        }
    }
}
